import React, { useState } from 'react';
import PlantillaUno from '../PlantillaUno';
import axiosInstance from '../../api/axios';

const Register = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    username: '',
    address: '',
    phone: '',
    documentType: '',
    document: '',
  });

  const [errors, setErrors] = useState({});

  const validate = () => {
    const formErrors = {};

    if (formData.name.length < 3 || formData.name.length > 20) {
      formErrors.name = 'Nombre debe tener entre 3 y 20 caracteres.';
    }
    if (!/\S+@\S+\.\S+/.test(formData.email)) {
      formErrors.email = 'Correo electrónico no es válido.';
    }
    if (formData.username.length < 5 || formData.username.length > 15) {
      formErrors.username = 'Nombre de usuario debe tener entre 5 y 15 caracteres.';
    }
    if (formData.address.length < 10 || formData.address.length > 30) {
      formErrors.address = 'La dirección debe tener entre 10 y 30 caracteres.';
    }
    if (!/^[0-9]{10}$/.test(formData.phone)) {
      formErrors.phone = 'Número de teléfono debe tener 10 dígitos.';
    }
    if (!formData.documentType) {
      formErrors.documentType = 'Debes seleccionar un tipo de documento.';
    }
    if (!/^[0-9]{10}$/.test(formData.document)) {
      formErrors.document = 'Número de identificación debe tener 10 dígitos.';
    }

    setErrors(formErrors);
    return Object.keys(formErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validate()) return;

    try {
      const response = await axiosInstance.post('/api/v1/oxi/individual/add', {
        data: { ...formData, document_type_id: formData.documentType },
      });
      console.log('Registro exitoso:', response.data);
      alert('Registro exitoso');
    } catch (error) {
      console.error('Error en el registro:', error);
      alert('Error en el registro. Intenta nuevamente.');
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({ ...prevData, [name]: value }));
  };

  return (
    <PlantillaUno title="Registro">
      <div className="container">
        <h1 className="text-center">Regístrate</h1>
        <form onSubmit={handleSubmit} noValidate>
          {['name', 'email', 'username', 'address', 'phone', 'document'].map((field) => (
            <div key={field} className="mb-3">
              <label className="form-label" htmlFor={field}>{field.toUpperCase()}</label>
              <input
                type={field === 'email' ? 'email' : 'text'}
                className={`form-control ${errors[field] ? 'is-invalid' : ''}`}
                name={field}
                id={field}
                onChange={handleChange}
                value={formData[field]}
              />
              {errors[field] && <div className="invalid-feedback">{errors[field]}</div>}
            </div>
          ))}
          <div className="mb-3">
            <label className="form-label" htmlFor="documentType">Tipo de documento</label>
            <select
              className={`form-select ${errors.documentType ? 'is-invalid' : ''}`}
              id="documentType"
              name="documentType"
              onChange={handleChange}
              value={formData.documentType}
            >
              <option value="">Escoje...</option>
              <option value="1">Cédula de ciudadanía</option>
              <option value="2">Cédula de extranjería</option>
              <option value="3">NIT</option>
            </select>
            {errors.documentType && <div className="invalid-feedback">{errors.documentType}</div>}
          </div>
          <button type="submit" className="btn btn-primary w-100">Registrarse</button>
        </form>
      </div>
    </PlantillaUno>
  );
};

export default Register;
