import React, { useState } from 'react';
import PlantillaUno from '../PlantillaUno';
import axiosInstance from '../../api/axios';

const Register = () => {
  const initialFormState = {
    name: '',
    email: '',
    username: '',
    address: '',
    phone: '',
    documentType: '',
    document: '',
    individualType: ''
  };

  const [formData, setFormData] = useState(initialFormState);
  const [errors, setErrors] = useState({});

  const validate = () => {
    const formErrors = {};
    if (formData.name.length < 3 || formData.name.length > 20) formErrors.name = 'Nombre debe tener entre 3 y 20 caracteres.';
    if (!/\S+@\S+\.\S+/.test(formData.email)) formErrors.email = 'Correo electrónico no es válido.';
    if (formData.username.length < 5 || formData.username.length > 15) formErrors.username = 'Nombre de usuario debe tener entre 5 y 15 caracteres.';
    if (formData.address.length < 10 || formData.address.length > 30) formErrors.address = 'La dirección debe tener entre 10 y 30 caracteres.';
    if (!/^[0-9]{10}$/.test(formData.phone)) formErrors.phone = 'Número de teléfono debe tener 10 dígitos.';
    if (!formData.documentType) formErrors.documentType = 'Debes seleccionar un tipo de documento.';
    if (!/^[0-9]{10}$/.test(formData.document)) formErrors.document = 'Número de identificación debe tener 10 dígitos.';
    if (!formData.individualType) formErrors.individualType = 'Debes seleccionar si eres cliente o empresa.';

    setErrors(formErrors);
    return Object.keys(formErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validate()) return;

    try {
      const individualResponse = await axiosInstance.post("/api/v1/oxi/individual/add", {
        data: {
          name: formData.name,
          document_type_id: formData.documentType,
          individual_type_id: formData.individualType
        }
      });

      const individualId = individualResponse.data.id;

      await axiosInstance.post("/api/v1/oxi/user/add", {
        data: {
          username: formData.email,
          password: formData.document,
          email: formData.email,
          address: formData.address,
          phone: formData.phone,
          individual_id: individualId,
          state: true,
          rol_type_id: 1
        }
      });

      alert("Registro exitoso");
      setFormData(initialFormState);
    } catch (error) {
      if (error.response) {
        const { status, data } = error.response;
        if (status === 400) {
          alert("Datos inválidos: " + (data.message || "Revisa el formulario."));
        } else if (status === 409) {
          alert("El correo o documento ya están registrados.");
        } else {
          alert("Error en el registro. Intenta nuevamente.");
        }
      } else {
        alert("Error de conexión. Verifica tu internet.");
      }
      console.error("Error en el registro:", error);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevData => ({ ...prevData, [name]: value }));
  };

  return (
    <PlantillaUno title="Registro">
      <div className="row">
        <div className="container-fluid backformulario ps-0 pe-0">
          <div className="row m-0">
            <div className="col-md-3"></div>
            <div className="col-md-6 pe-0 ps-0">
              <div className="card shadow-lg p-3 m-5 body-tertiary rounded">
                <div className="card-body">
                  <h1 className="text-center seccion-titulo">Regístrate</h1>
                  <p className="text-center seccion-descripcion">Completa el formulario para poder registrarte</p>
                  <form className="row g-3 needs-validation" onSubmit={handleSubmit} noValidate>
                    <div className="col-12 d-flex justify-content-center mt-4">
                      <button type="submit" className="btn btn-secondary w-50">Registrarse</button>
                    </div>
                  </form>
                </div>
              </div>
            </div>
            <div className="col-md-3"></div>
          </div>
        </div>
      </div>
    </PlantillaUno>
  );
};

export default Register;
